import React, { useState, FC} from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

import LoginPage,{UserState} from './pages/login-page';
import NotFoundPage from './pages/not-found-page';


const App: FC = () => {
  const [token, setToken] = useState<string>("");

  const handleLoginSuccess = (user?: UserState) => {
    if (user) {
      setToken(user.username);
    }
  }
  if (!token)
    return <Route path="/login" element={<LoginPage onLoginSuccess={handleLoginSuccess}/>}></Route>

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/dashboard" element={<h1>Welcome to the Dashboard</h1>} />
        <Route key="not-found" path="*" element={<NotFoundPage/>} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
