import React,{useState} from 'react';

export interface UserState{
  username : string,
  password : string
}

interface LoginPageProps {
  onLoginSuccess: (user: UserState) => void;
}

function LoginPage ({ onLoginSuccess }: LoginPageProps) {
  const [userState, setUserState] = useState<UserState>({
    username: "",
    password: "",
  });

  const handleFormSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    // Effettua la chiamata all'API per verificare le credenziali
    // e recuperare le informazioni dell'utente, ad esempio:
    const user = {
      username: 'username',
      password : 'password',
    };
    onLoginSuccess(user);
  };


  return (
    <div className="w-screen h-screen flex flex-col bg-black">

    <div className="flex-1 flex  justify-center bg-white" style={{ clipPath: "polygon(0% 100%, 0% -30%, 100% 100%)"}}>
      <div className="w-1/4 z-50 flex items-center justify-center">
        <form className='w-50 container bg-red' onSubmit={handleFormSubmit}>
          <h1 className='text-4xl'>Login</h1>
          {/*Email input */}
          <label className="block">
            <span className="after:content-['*'] after:ml-0.5 after:text-red-500 block text-sm font-medium text-slate-700">
              Email
            </span>
            <input type="email" name="email" className="mt-1 px-3 py-2 bg-white border shadow-sm border-slate-300 placeholder-slate-400 focus:outline-none focus:border-sky-500 focus:ring-sky-500 block w-full rounded-md sm:text-sm focus:ring-1" placeholder="you@example.com" onChange={(e) => setUserState({...userState,username : e.target.value})}/>
          </label>
          {/*Password Input */}
          <label className="block">
            <span className="after:content-['*'] after:ml-0.5 after:text-red-500 block text-sm font-medium text-slate-700">
              Password
            </span>
            <input type="password" name="password" className="mt-1 px-3 py-2 bg-white border shadow-sm border-slate-300 placeholder-slate-400 focus:outline-none focus:border-sky-500 focus:ring-sky-500 block w-full rounded-md sm:text-sm focus:ring-1" placeholder="your password"  onChange={(e) => setUserState({...userState,password : e.target.value})}/>
          </label>
          {/*Button Login */}
          <button type="submit" className="bg-blue-500 mt-5 w-full rounded-r-lg rounded-l-lg py-1" >
            Login
          </button>
        </form>
      </div>
      <div className="w-1/2 ">
        <div className="absolute inset-x-0 bottom-0 mr-8 text-1xl">
          <h3 className='text-end text-black'>Develope by <a>variety studios</a></h3>
        </div>
      </div>
    </div>
  </div>
);

};

export default LoginPage;
